function cleanStatus(status){
	var p = status.indexOf(":");
	if(p>-1){
		status = status.slice(0,p);
	}
	return status;
}
function cleanType(type){
	var p = type.indexOf("(");
	if(p>-1){
		type = type.slice(0,p);
	}
	return type;
}
